// Parse AI response to extract queries
const aiResponse = $input.first().json;
let text = aiResponse.message?.content || aiResponse.text || JSON.stringify(aiResponse);

// Try to extract JSON from the response
const jsonMatch = text.match(/\{[\s\S]*\}/);
let queries = [];

if (jsonMatch) {
  try {
    const parsed = JSON.parse(jsonMatch[0]);
    queries = parsed.queries || [];
  } catch(e) {
    // Fallback: split by newlines
    queries = text.split('\n').filter(q => q.trim().length > 3).slice(0, 8);
  }
}

const context = $('Webhook Trigger').first().json.body.context || '';

return queries.map(q => ({ json: { query: q, context: context } }));

// Collect all SERP results, deduplicate by domain, extract candidate keywords
const allItems = $input.all();
const seenDomains = new Set();
const articles = [];
const queriesUsed = [];

for (const item of allItems) {
  const tasks = item.json.tasks || [];
  for (const task of tasks) {
    if (task.data?.keyword) queriesUsed.push(task.data.keyword);
    const results = task.result || [];
    for (const resultSet of results) {
      const items_arr = resultSet.items || [];
      for (const r of items_arr) {
        if (r.type !== 'organic') continue;
        const domain = r.domain || '';
        if (seenDomains.has(domain)) continue;
        seenDomains.add(domain);
        articles.push({
          url: r.url || '',
          title: r.title || '',
          description: r.description || '',
          position: r.rank_absolute || 0,
          domain: domain
        });
      }
    }
  }
}

// Take top 20 most relevant
articles.sort((a, b) => a.position - b.position);
const topArticles = articles.slice(0, 20);

// Extract candidate keywords from titles
const stopWords = new Set(['il','lo','la','i','gli','le','un','una','di','del','dello','della','dei','degli','delle','a','al','allo','alla','ai','agli','alle','da','dal','dallo','dalla','dai','dagli','dalle','in','nel','nello','nella','nei','negli','nelle','con','su','sul','sullo','sulla','sui','sugli','sulle','per','tra','fra','e','o','ma','che','come','quando','se','non','più','molto','anche','sono','è','ha','ho','hanno','essere','avere']);

const keywordCandidates = [];
for (const article of topArticles) {
  const words = article.title.toLowerCase()
    .replace(/[^a-zàèéìòù0-9\s]/gi, '')
    .split(/\s+/)
    .filter(w => w.length > 2 && !stopWords.has(w));

  // 2-3 word combinations
  for (let i = 0; i < words.length - 1; i++) {
    keywordCandidates.push(words[i] + ' ' + words[i+1]);
    if (i < words.length - 2) {
      keywordCandidates.push(words[i] + ' ' + words[i+1] + ' ' + words[i+2]);
    }
  }
}

// Deduplicate and take top 15
const uniqueKws = [...new Set(keywordCandidates)].slice(0, 15);

const context = $('Webhook Trigger').first().json.body.context || '';

return [{ json: {
  articles: topArticles,
  candidateKeywords: uniqueKws,
  queriesUsed: queriesUsed,
  context: context
} }];

// Parse keyword data and merge with SERP data
const keywordResponse = $input.first().json;
const prevData = $('Raccogli e Deduplica').first().json;

const tasks = keywordResponse.tasks || [];
const task = tasks[0] || {};
const results = task.result || [];

const keywordData = {};
for (const r of results) {
  keywordData[r.keyword] = {
    volume: r.search_volume || 0,
    competition: r.competition || 'UNSPECIFIED',
    cpc: r.cpc || 0,
    competition_index: r.competition_index || 0
  };
}

return [{ json: {
  articles: prevData.articles,
  keywordData: keywordData,
  queriesUsed: prevData.queriesUsed,
  context: prevData.context
} }];

// Parse AI ideas response and format final output
const aiResponse = $input.first().json;
const prevData = $('Merge Keyword Data').first().json;

let text = aiResponse.message?.content || aiResponse.text || JSON.stringify(aiResponse);

// Extract JSON from response
const jsonMatch = text.match(/\{[\s\S]*\}/);
let ideas = [];
let strategyNotes = '';

if (jsonMatch) {
  try {
    const parsed = JSON.parse(jsonMatch[0]);
    ideas = parsed.ideas || [];
    strategyNotes = parsed.strategy_notes || '';
  } catch(e) {
    return [{ json: {
      success: false,
      error: 'Impossibile parsare la risposta AI: ' + e.message
    } }];
  }
} else {
  return [{ json: {
    success: false,
    error: 'Nessun JSON trovato nella risposta AI'
  } }];
}

// Validate and clean ideas
ideas = ideas.map((idea, i) => ({
  title: idea.title || 'Idea ' + (i + 1),
  subtitle: idea.subtitle || '',
  description: idea.description || '',
  target_keywords: Array.isArray(idea.target_keywords) ? idea.target_keywords : [],
  primary_keyword: idea.primary_keyword || '',
  estimated_volume: parseInt(idea.estimated_volume) || 0,
  difficulty: idea.difficulty || 'media',
  content_type: idea.content_type || 'guida',
  priority: parseInt(idea.priority) || (i + 1)
}));

return [{ json: {
  success: true,
  ideas: ideas,
  strategy_notes: strategyNotes,
  meta: {
    total_ideas: ideas.length,
    queries_used: prevData.queriesUsed || [],
    competitor_articles_analyzed: (prevData.articles || []).length,
    timestamp: new Date().toISOString()
  }
} }];

